import { Button, Table } from "reactstrap";

const TablaContacto = ({ data, setEditar, mostrarModal, setMostrarModal }) => {

    const enviarDatos = (contacto) => {
        setEditar(contacto)
        setMostrarModal(!mostrarModal)
    }

    return (
        <Table striped responsive>
            <thead>
                <tr>
                    <th>Nombre</th>
                    <th>Correo</th>
                    <th>Telefono</th>
                    <th></th>
                </tr>
            </thead>
            <tbody>
                {

                    (data.length < 1) ? (
                        <tr>
                            <td colSpan="4">No records</td>
                        </tr>
                    ) : (
                        data.map((item) => (

                            <tr key={item.idContacto}>
                                <td>{item.nombre}</td>
                                <td>{item.correo}</td>
                                <td>{item.telefono}</td>
                                <td>
                                    <Button color="primary" size="sm" className="me-2"
                                        onClick={() => enviarDatos()}
                                    >Editar</Button>
                                    <Button color="danger" size="sm">Eliminar</Button>
                                </td>
                            </tr>
                        ))
                    )
                }
            </tbody>
        </Table>
    )
}

export default TablaContacto;
