import { useEffect, useState } from 'react'
import { Modal, ModalBody, ModalHeader, Form, FormGroup, Input, Label, ModalFooter, Button } from "reactstrap"

const modelMovie = {
    id: 0,
    title: ""
}

const MovieModal = ({ showModal, setShowModal, saveMovie, edit, setEdit, editMovie }) => {

    const [movie, setMovie] = useState(modelMovie);

    const updateData = (e) => {

        console.log(e.target.name + " : " + e.target.value)
        setMovie(
            {
                ...movie,
                [e.target.name]: e.target.value
            }
        )
    }

    const sendData = () => {

        if (movie.id == 0) {
            saveMovie(movie)
        } else {
            editMovie(movie)
        }

        setMovie(modelMovie)
    }

    useEffect(() => {
        if (edit != null) {
            setMovie(edit)
        } else {
            setMovie(modelMovie)
        }
    }, [edit])


    const closeModal = () => {

        setShowModal(!showModal)
        setEdit(null)
    }

    return (

        <Modal isOpen={showModal}>
            <ModalHeader>

                {movie.id == 0 ? "New Movie" : "Edit Movie"}

            </ModalHeader>
            <ModalBody>
                <Form>
                    <FormGroup>
                        <Label>Title</Label>
                        <Input name="title" onChange={(e) => updateData(e)} value={movie.title} />
                    </FormGroup>
                </Form>
            </ModalBody>
            <ModalFooter>
                <Button color="primary" size="sm" onClick={sendData}>Save</Button>
                <Button color="danger" size="sm" onClick={closeModal}>Close</Button>
            </ModalFooter>
        </Modal>
    )

}

export default MovieModal;
