import { useState } from 'react'
import { Modal, ModalBody, ModalHeader, Form, FormGroup, Input, Label, ModalFooter, Button } from "reactstrap"

const modelDeparment = {
    idDepartment: 0,
    name: ""
}

const DepartmentModal = ({showModal,setShowModal,saveDepartment }) => {

    const [department, setDepartment] = useState(modelDeparment);

    const updateData = (e) => {

        console.log(e.target.name + " : " + e.target.value)
        setDepartment(
            {
                ...department,
                [e.target.name]: e.target.value
            }
        )
    }

    return (

        <Modal isOpen={true}>
            <ModalHeader>
                New Department
            </ModalHeader>
            <ModalBody>
                <Form>
                    <FormGroup>
                        <Label>Name</Label>
                        <Input name="name" onChange={(e) => updateData(e)} value={department.name} />
                    </FormGroup>
                </Form>
            </ModalBody>
            <ModalFooter>
                <Button color="primary" size="sm">Save</Button>
                <Button color="danger" size="sm">Close</Button>
            </ModalFooter>
        </Modal>
    )

}

export default DepartmentModal;
