import { useEffect, useState } from "react"
import { Modal, ModalBody, ModalHeader, Form, FormGroup, Input, Label, ModalFooter, Button } from "reactstrap"

const modelDepartment = {
    idDepartment: 0,
    departmentname: ""
}


const DepartmentModal = ({ showModal, setShowModal, saveDepartment, edit, setEdit, editDepartment }) => {

    const [department, setDepartment] = useState(modelDepartment);

    const updateData = (e) => {

        console.log(e.target.departmentname + " : " + e.target.value)

        setDepartment(
            {
                ...department,
                [e.target.departmentname]: e.target.value
            }
        )

    }

    const sendData = () => {

        if (department.idDepartment == 0) {
            saveDepartment(department)
        } else {
            editDepartment(department)
        }

        setDepartment(modelDepartment)

    }

    useEffect(() => {
        if (edit != null) {
            setDepartment(edit)
        } else {
            setDepartment(modelDepartment)
        }

    }, [edit])

    const closeModal = () => {

        setShowModal(!showModal)
        setEdit(null)
    }

    return (

        <Modal isOpen={showModal}>
            <ModalHeader>
            New Department
             {/*  {department.idDepartment == 0 ? "New Department" : "Edit Department"}*/}
                
            </ModalHeader>
            <ModalBody>
                <Form>
                    <FormGroup>
                        <Label>Name</Label>
                        <Input name="departmentname" onChange={(e) => updateData(e)} value={department.departmentname} />
                    </FormGroup>
                </Form>
            </ModalBody>
            <ModalFooter>
                <Button color="primary" size="sm" onClick={sendData }>Save</Button>
                <Button color="danger" size="sm" onClick={closeModal }>Close</Button>
            </ModalFooter>
        </Modal>
    )

}

export default DepartmentModal;
