import { useEffect, useState } from "react"
import { Col, Container, Row, Card, CardHeader, CardBody, Button } from "reactstrap"
import ModalContacto from "./components/ModalContacto"
import TablaContacto from "./components/TablaContacto"

const App = () => {

    const [contactos, setContactos] = useState([])
    const [mostrarModal, setmostrarModal] = useState(false);

    const mostrarContactos = async () => {

        const response = await fetch("api/contacto/Lista")

        if (response.ok) {
            const data = await response.json();
            setContactos(data)
        } else {
            console.log("error en la lista")
        }

    }

    useEffect(() => {
        mostrarContactos()
    }, [])


    return (
        <Container>
            <Row className="mt-5">
                <Col sm="12">
                    <Card>
                        <CardHeader>
                            <h5>Lista de contacto</h5>
                        </CardHeader>
                        <CardBody>
                            <Button size="sm" color="success">Nuevo Contacto</Button>
                            <hr></hr>
                            <TablaContacto data={contactos} />
                        </CardBody>
                    </Card>
                </Col>
            </Row>

            <ModalContacto/>
        </Container>
    )
}

export default App;
