import { useEffect, useState } from 'react'
import { Modal, ModalBody, ModalHeader, Form, FormGroup, Input, Label, ModalFooter, Button } from "reactstrap"
import CastingTable from "./components/CastingTable";


const modelMovie = {
    movieid: 0,
    title: ""
}

const MovieModal = ({ showModal, setShowModal, saveMovie, edit, setEdit, editMovie }) => {


    const [movie, setMovie] = useState(modelMovie);
    //const parsed_link = JSON.parse(movie.castbymovie);
    console.log(movie);

    const updateData = (e) => {

        console.log(e.target.name + " : " + e.target.value)

        setMovie(
            {
                ...movie,
                [e.target.name]: e.target.value
            }
        )
    }

    const sendData = () => {

        if (movie.movieId == 0) {
            saveMovie(movie)
        } else {
            editMovie(movie)
        }

        setMovie(modelMovie)
    }

    useEffect(() => {
        if (edit != null) {
            setMovie(edit)
        } else {
            setMovie(modelMovie)
        }
    }, [edit])


    const closeModal = () => {

        setShowModal(!showModal)
        setEdit(null)
    }

    return (

        <Modal isOpen={showModal}>
            <ModalHeader>
                {
                    /*{movie.id == 0 ? "New Movie" : "Edit Movie"} */
                    "Movie Details"
                }
            </ModalHeader>
            <ModalBody>
                <Form>
                    <div>
                        <p>Movie Id: {movie.movieId} </p>
                        <p>Title: {movie.title} </p>                    
                        <p>Genre: {movie.genre} </p>
                        <p>Main Cast</p>
                    </div>              
                    <CastingTable data={movies}
                        setEdit={setEdit}
                        showModal={showModal}
                        setShowModal={setShowModal}
                    />
                </Form>
            </ModalBody>
            <ModalFooter>             
                <Button color="danger" size="sm" onClick={closeModal}>Close</Button>
            </ModalFooter>
        </Modal>
    )

}

export default MovieModal;
