import { useEffect, useState } from "react"
import { Col, Container, Row, Card, CardHeader, CardBody, Button } from "reactstrap"
import MovieModal from "./components/MovieModal";
import MovieTable from "./components/MovieTable";
import CastingModal from "./components/CastingModal";
import CastingTable from "./components/CastingTable";


const App = () => {

    const [movies, setMovies] = useState([])
    const [casting, setCastings] = useState([])
    const [showModal, setShowModal] = useState(false);
    const [edit, setEdit] = useState(null)

    const showMovies = async () => {

        const response = await fetch("api/movie/GetMovies")

        if (response.ok) {
            const data = await response.json();
            setMovies(data)
        } else {
            console.log("error fetching data")
        }

    }

    useEffect(() => {
        showMovies()
    }, [])


    const saveMovie = async (movie) => {
        const response = await fetch("api/movie/GetMovies", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json;charset=utf-8'
            },
            body: JSON.stringify(movie)
        })

        if (response.ok) {
            setShowModal(!showModal);
            showMovies();
        }
    }

    /*-------------------------------------------------------*/

    const editMovie = async (movie) => {
        const response = await fetch("api/department/EditMovie", {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json;charset=utf-8'
            },
            body: JSON.stringify(movie)
        })

        if (response.ok) {
            setShowModal(!showModal);
            showMovies();
        }
    }

    /*---------------------------------------------*/

    const deleteMovie = async (id) => {
        var userchoice = window.confirm("are you sure to delete this item?")

        if (!userchoice) {
            return;
        }

        const response = await fetch("api/department/DeleteMovie/" + id, {
            method: 'DELETE',
        })

        if (response.ok) {
            setShowModal(false);
            showMovies();
        }
    }


    return (
        <Container>
            <Row className="mt-5">
                <Col sm="12">
                    <Card>
                        <CardHeader>
                            <h5>Movies</h5>
                        </CardHeader>
                        <CardBody>
                            <Button size="sm" color="success"
                                onClick={() => setShowModal(!showModal)}
                            >New Movie</Button>
                            <hr></hr>
                            <MovieTable data={movies}
                                setEdit={setEdit}
                                showModal={showModal}
                                setShowModal={setShowModal}

                                deleteMovie={deleteMovie}
                            />
                        </CardBody>
                    </Card>
                </Col>
            </Row>
            <MovieModal
               /* showModal={showModal}

                setShowModal={setShowModal}
                saveMovie={saveMovie}

                edit={edit}
                setEdit={setEdit}
                editMovie={editMovie}*/
            />
        </Container>
    )
}
export default App;
