import { useEffect, useState } from "react"
import { Col, Container, Row, Card, CardHeader, CardBody, Button } from "reactstrap"
import ModalContacto from "./components/ModalContacto"
import TablaContacto from "./components/TablaContacto"

const App = () => {

    const [contactos, setContactos] = useState([])
    const [mostrarModal, setmostrarModal] = useState(false);
    const [editar, setEditar] = useState(null);

    const mostrarContactos = async () => {

        const response = await fetch("api/contacto/Lista")

        if (response.ok) {
            const data = await response.json();
            setContactos(data)
        } else {
            console.log("error en la lista")
        }

    }

    useEffect(() => {
        mostrarContactos()
    }, [])

    const guardarContacto = async(contacto) => {

         const response = await fetch("api/contacto/Guardar", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json;charset=utf-8'
             },
             body: JSON.stringify(contacto)
         })

        if (response.ok) {
            setmostrarModal(!mostrarModal);
            mostrarContactos();
        }

    }

    const editarContacto = async (contacto) => {

        const response = await fetch("api/contacto/Editar", {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json;charset=utf-8'
            },
            body: JSON.stringify(contacto)
        })

        if (response.ok) {
            setmostrarModal(!mostrarModal);
            mostrarContactos();
        }

    }

    const eliminarContacto = async (id) => {

        var respuesta = window.confirm("desea eliminar el contacto?")

        if (!respuesta) {
            return;
        }

        const response = await fetch("api/contacto/Eliminar" + id, {
            method: 'DELETE'
        })

        if (response.ok) {           
            mostrarContactos();
        }

    }



    return (
        <Container>
            <Row className="mt-5">
                <Col sm="12">
                    <Card>
                        <CardHeader>
                            <h5>Lista de contacto</h5>
                        </CardHeader>
                        <CardBody>
                            <Button size="sm" color="success"
                                onClick={() => setmostrarModal(!mostrarModal)}
                            >Nuevo Contacto</Button>
                            <hr></hr>
                            <TablaContacto data={contactos}
                                setEditar={setEditar}
                                mostrarModal={mostrarModal}
                                setMostrarModal={setmostrarModal}

                                eliminarContacto={eliminarContacto}
                            />
                        </CardBody>
                    </Card>
                </Col>
            </Row>

            <ModalContacto
                mostrarModal={mostrarModal}
                setMostrarModal={setmostrarModal}
                guardarContacto={guardarContacto}

                editar={editar}
                setEditar={setEditar}
                editarContacto={editarContacto}
                eliminarContacto={eliminarContacto}
            />
        </Container>
    )
}

export default App;
