import { useEffect, useState } from "react"
import { Col, Container, Row, Card, CardHeader, CardBody, Button } from "reactstrap"
import DepartmentModal from "./components/DepartmentModal";
import DepartmentTable from "./components/DepartmentTable";


const App = () => {

    const [departments, setDepartments] = useState([])
    const [showModal, setShowModal] = useState(false)

    const showDepartments = async () => {

        const response = await fetch("api/department/GetDepartments")

        if (response.ok) {
            const data = await response.json();
            setDepartments(data)
        } else {
            console.log("error fetching data")
        }

    }

    useEffect(() => {
        showDepartments()
    }, [])


    const saveDepartment = async (deparment) => {

        const response = await fetch("api/deparment/SaveDepartment", {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json;charset=utf-8'
            },
            body: JSON.stringify(deparment)
        })

        if (response.ok) {
            setShowModal(!showModal);
            showDepartments();
        }

    }


    return (
        <Container>
            <Row className="mt-5">
                <Col sm="12">
                    <Card>
                        <CardHeader>
                            <h5>Departments</h5>
                        </CardHeader>
                        <CardBody>
                            <Button size="sm" color="success"
                                onClick={() => setShowModal(!showModal)}
                            >New Department</Button>
                            <hr></hr>
                            <DepartmentTable data={departments} />
                        </CardBody>
                    </Card>
                </Col>
            </Row>

            <DepartmentModal

            />
        </Container>
    )
}

export default App;
